    const sendMessageToGemini = async (message, history) => {
      if (!ai) {
        return new Promise((resolve) => {
          setTimeout(() => {
            resolve("أنا مساعد ذكي تجريبي. يرجى ضبط مفتاح API في كود القالب للتحدث بشكل حقيقي!");
          }, 1000);
        });
