      try {
        const formattedHistory = history.map(h => ({
          role: h.role,
          parts: [{ text: h.text }]
        }));

        const chat = ai.chats.create({
          model: 'gemini-2.5-flash-latest',
          config: {
            systemInstruction: `أنت المساعد الذكي لشركة "Creative Frame" (الإطار الإبداعي)، وهي وكالة إبداعية متميزة في الشرق الأوسط.
            يجب أن يكون ردك دائماً باللغة العربية، بأسلوب احترافي، سينمائي، ومبدع وجذاب.
            خدماتنا تشمل: مونتاج الفيديو، الموشن جرافيك، التصميم الجرافيكي، التسويق الرقمي، التصوير الفوتوغرافي.
            تعليمات: تحدث بلهجة ترحيبية، جاوب بإيجاز، شجع العملاء على التواصل.`,
          },
          history: formattedHistory
        });

        const result = await chat.sendMessage({ message: message });
        return result.text || "";
      } catch (error) {
        console.error("Gemini Error", error);
        return "عذراً، أواجه مشكلة بسيطة في الاتصال بالخادم الإبداعي حالياً.";
