      useEffect(() => {
        if (isInView) {
          const duration = 2000; const steps = 60; const stepTime = duration / steps;
          let current = 0; const increment = target / steps;
          const timer = setInterval(() => { current += increment; if (current >= target) { setCount(target); clearInterval(timer); } else { setCount(Math.floor(current)); } }, stepTime);
          return () => clearInterval(timer);
        }
      }, [isInView, target]);
