    const AppContent = () => {
      const [lang, setLang] = useState('ar');
      const [user, setUser] = useState(null);
      useEffect(() => {
        document.documentElement.dir = lang === 'en' ? 'ltr' : 'rtl';
        document.documentElement.lang = lang;
        if (lang === 'ar') { document.body.classList.remove('font-poppins'); document.body.classList.add('font-cairo'); } 
        else { document.body.classList.remove('font-cairo'); document.body.classList.add('font-poppins'); }
      }, [lang]);
      useEffect(() => { const storedUser = localStorage.getItem('creative_frame_user'); if (storedUser) { setUser(JSON.parse(storedUser)); } }, []);
      const handleLogin = (newUser) => { setUser(newUser); localStorage.setItem('creative_frame_user', JSON.stringify(newUser)); };
      const handleLogout = () => { setUser(null); localStorage.removeItem('creative_frame_user'); };

      return (






